var gulp = require('gulp')
var fs = require('fs')
var s3 = require("./index");
var gzip = require("gulp-gzip");

var aws = JSON.parse(fs.readFileSync('./aws.json'));
var options = { };
gulp.task('default', function() {
    return gulp.src('./files/**/*')
        .pipe(gzip())
        .pipe(s3(aws, options));
})
